Observation.cart=function(){$("img.product_list").addClass("draggable").each(function(){$(this).draggable({helper:"clone",opacity:0.5,zIndex:10})});$("#cart_items").droppable({accept:"img.product_list",hoverClass:"ondrop",drop:function(a,b){a=b.draggable.attr("alt");ajax({url:CONTEXT_PATH+"/cart/add/"+a,type:"POST",replacement:"cart_items"})}})};
Initialization.app=function(){typeof $.fn.autocomplete!="undefined"&&$("#q").length>0&&$("#q").autocomplete(CONTEXT_PATH+"/search/suggest?decorator=none",{minChars:3,delay:1E3,formatItem:function(a){return a[0]+"<cite>"+a[1]+"</cite>"},formatResult:function(a){return a[0]}});$("a.category").each(function(){this.onprepare=function(){$("a.category").each(function(){$(this).removeClass("selected")});$(this).addClass("selected")}})};function login(){};
