Date.prototype.format = function(fmt, monthNames, dayNames) {
	if (typeof this.strftime == "function") {
		return this.strftime(fmt);
	}
	var leftPad = function(n, pad) {
		n = "" + n;
		pad = "" + (pad == null ? "0" : pad);
		return n.length == 1 ? pad + n : n;
	};

	var r = [];
	var escape = false;
	var hours = this.getHours();
	var isAM = hours < 12;
	if (monthNames == null)
		monthNames = ["Jan", "Feb", "Mar", "Apr", "May", "Jun", "Jul", "Aug",
				"Sep", "Oct", "Nov", "Dec"];
	if (dayNames == null)
		dayNames = ["Sun", "Mon", "Tue", "Wed", "Thu", "Fri", "Sat"];

	var hours12;
	if (hours > 12) {
		hours12 = hours - 12;
	} else if (hours == 0) {
		hours12 = 12;
	} else {
		hours12 = hours;
	}

	for (var i = 0; i < fmt.length; ++i) {
		var c = fmt.charAt(i);

		if (escape) {
			switch (c) {
				case 'a' :
					c = "" + dayNames[this.getDay()];
					break;
				case 'b' :
					c = "" + monthNames[this.getMonth()];
					break;
				case 'd' :
					c = leftPad(this.getDate());
					break;
				case 'e' :
					c = leftPad(this.getDate(), " ");
					break;
				case 'H' :
					c = leftPad(hours);
					break;
				case 'I' :
					c = leftPad(hours12);
					break;
				case 'l' :
					c = leftPad(hours12, " ");
					break;
				case 'm' :
					c = leftPad(this.getMonth() + 1);
					break;
				case 'M' :
					c = leftPad(this.getMinutes());
					break;
				case 'S' :
					c = leftPad(this.getSeconds());
					break;
				case 'y' :
					c = leftPad(this.getFullYear() % 100);
					break;
				case 'Y' :
					c = "" + this.getFullYear();
					break;
				case 'p' :
					c = (isAM) ? ("" + "am") : ("" + "pm");
					break;
				case 'P' :
					c = (isAM) ? ("" + "AM") : ("" + "PM");
					break;
				case 'w' :
					c = "" + this.getDay();
					break;
			}
			r.push(c);
			escape = false;
		} else {
			if (c == "%")
				escape = true;
			else
				r.push(c);
		}
	}
	return r.join("");
};
